args:
.status-message {
  margin-top: 15px;
  padding: 12px;
  background-color: #1e293b;
  border: 1px solid #334155;
  border-radius: 6px;
  font-size: 0.9rem;
}

.status-message strong {
  display: block;
  margin-bottom: 8px;
  color: #60a5fa;
  font-size: 1rem;
}

.stat-row {
  display: flex;
  justify-content: space-between;
  margin-bottom: 4px;
  color: #cbd5e1;
}

.stat-bar-container {
  display: flex;
  height: 8px;
  width: 100%;
  background-color: #334155;
  border-radius: 4px;
  overflow: hidden;
  margin: 8px 0;
}

.stat-bar-fill {
  height: 100%;
}

.stat-bar-fill.used {
  background-color: #10b981;
}

.stat-bar-fill.empty {
  background-color: #ef4444;
}

.time-row {
  margin-top: 8px;
  font-size: 0.8rem;
  color: #94a3b8;
  border-top: 1px solid #334155;
  padding-top: 4px;
}

.box-list-header {
  display: flex;
  gap: 10px;
  padding: 0 10px 5px;
  font-size: 0.8rem;
  color: #94a3b8;
  font-weight: 600;
  margin-bottom: 5px;
  border-bottom: 1px solid #334155;
}

.box-list-header span {
   text-align: center;
}

/* Match widths with renderBoxItem inputs */
.col-w, .col-h, .col-d {
  width: 50px;
}
.col-q {
  width: 80px; 
  flex-grow: 1;
}
.col-x {
  width: 24px;
}

.box-list-header {
  display: flex;
  gap: 8px; /* Matched to box-item gap */
  padding: 0 10px 5px;
  font-size: 0.8rem;
  color: #94a3b8;
  font-weight: 600;
  margin-bottom: 5px;
  border-bottom: 1px solid #334155;
}

.box-list-header span {
   text-align: center;
}

/* Match widths with renderBoxItem inputs */
.col-w, .col-h, .col-d {
  width: 50px;
}
.col-q {
  width: 50px; 
  flex-grow: 1;
}
.col-x {
  width: 24px;
}
.failure-reason {
  color: #ef4444;
  font-size: 0.8rem;
  margin-bottom: 8px;
  padding-bottom: 8px;
  border-bottom: 1px dashed #4b5563;
}

/* Adjust box item for labels */
.box-item {
  display: flex;
  align-items: center;
  gap: 5px; /* Tighter gap */
  padding: 8px; /* Slightly tighter padding */
  flex-wrap: wrap; /* Allow wrapping on very small screens */
}

.box-label {
  font-size: 0.8rem;
  color: #94a3b8;
  font-weight: 600;
  user-select: none;
}

.box-label.x-label {
  margin-left: 4px;
  margin-right: 0px;
  color: #60a5fa;
  font-size: 1rem;
}

/* Update inputs to fit with labels */
.box-item input {
  width: 45px; /* Slightly smaller width */
  padding: 4px 6px;
  text-align: center;
}

.box-item input.box-quantity {
    width: 50px;
}

.remove-box-btn {
    margin-left: auto;
}

/* Modal Styles */
.optimization-modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.7);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  opacity: 0;
  transition: opacity 0.3s ease;
}

.optimization-content {
  background-color: #1e293b;
  padding: 30px;
  border-radius: 8px;
  border: 1px solid #475569;
  text-align: center;
  min-width: 300px;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.5);
}

.optimization-content h3 {
  margin-bottom: 15px;
  color: #60a5fa;
  font-size: 1.2rem;
}

.optimization-content p {
  margin-bottom: 20px;
  color: #cbd5e1;
  font-size: 0.95rem;
}

.progress-bar {
  width: 100%;
  height: 10px;
  background-color: #334155;
  border-radius: 5px;
  overflow: hidden;
}

.progress-fill {
  height: 100%;
  background-color: #3b82f6;
  width: 0%;
  transition: width 0.3s ease;
}

/* Remove spinners from number inputs */
/* Chrome, Safari, Edge, Opera */
input[type=number]::-webkit-outer-spin-button,
input[type=number]::-webkit-inner-spin-button {
  -webkit-appearance: none;
  margin: 0;
}

/* Firefox */
input[type=number] {
  -moz-appearance: textfield;
}
